// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./DEXFactory.sol";
import "./DEXPool.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title DEXRouter
 * @notice Router contract for executing swaps and managing liquidity
 * @dev Simplified router to avoid stack too deep errors
 */
contract DEXRouter {
    address public immutable factory;
    
    event SwapExecuted(
        address indexed user,
        address[] path,
        uint256[] amounts
    );
    
    constructor(address _factory) {
        factory = _factory;
    }
    
    /**
     * @notice Swap exact tokens for tokens (multi-hop)
     */
    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        uint24[] calldata fees,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts) {
        require(deadline >= block.timestamp, "Expired");
        require(path.length >= 2, "Invalid path");
        require(path.length == fees.length + 1, "Path/fees mismatch");
        
        amounts = new uint256[](path.length);
        amounts[0] = amountIn;
        
        // Transfer first token from user
        IERC20(path[0]).transferFrom(msg.sender, address(this), amountIn);
        
        // Execute swaps along the path
        for (uint256 i = 0; i < path.length - 1; i++) {
            address pool = DEXFactory(factory).getPool(path[i], path[i + 1], fees[i]);
            require(pool != address(0), "Pool does not exist");
            
            // Approve pool
            IERC20(path[i]).approve(pool, amounts[i]);
            
            // Execute swap
            amounts[i + 1] = DEXPool(pool).swap(path[i], amounts[i], 0);
        }
        
        // Check final amount
        require(amounts[amounts.length - 1] >= amountOutMin, "Insufficient output");
        
        // Transfer final tokens to recipient
        IERC20(path[path.length - 1]).transfer(to, amounts[amounts.length - 1]);
        
        emit SwapExecuted(msg.sender, path, amounts);
    }
    
    /**
     * @notice Get amounts out for a given path
     */
    function getAmountsOut(
        uint256 amountIn,
        address[] calldata path,
        uint24[] calldata fees
    ) external view returns (uint256[] memory amounts) {
        require(path.length >= 2, "Invalid path");
        require(path.length == fees.length + 1, "Path/fees mismatch");
        
        amounts = new uint256[](path.length);
        amounts[0] = amountIn;
        
        for (uint256 i = 0; i < path.length - 1; i++) {
            address pool = DEXFactory(factory).getPool(path[i], path[i + 1], fees[i]);
            require(pool != address(0), "Pool does not exist");
            
            amounts[i + 1] = DEXPool(pool).getQuote(path[i], amounts[i]);
        }
    }
}

